import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { ActivatedRoute, ParamMap } from '@angular/router';
import { BackEndServiceService } from 'src/app/back-end-service.service';

@Component({
  selector: 'app-editproduct',
  templateUrl: './editproduct.component.html',
  styleUrls: ['./editproduct.component.scss']
})
export class EditproductComponent implements OnInit  {

  productId:any;
  public Productdataarray:any;
  EditProductForm:FormGroup;

  constructor(private route:ActivatedRoute,private fb:FormBuilder,private backendservice:BackEndServiceService)
  {
    this.route.paramMap.subscribe((params:ParamMap)=>{
      this.productId=params.get('id')
    })
    this.EditProductForm=this.fb.group({
      productId:[''],
      ProductName:[''],
      Description:['']
    })

  }
 ngOnInit(): void {
  this.backendservice.getproductbaseid(this.productId).subscribe((data:any)=>{
    this.Productdataarray=data;

    this.EditProductForm.setValue({
      productId:data.productId,
      ProductName:data.productName,
      Description:data.description
    })
  })

 }
 OnSubmit(){

 }



}
<h1 style="text-align: center;">Edit Product</h1>

<div class="container">
    <h1 style="padding-top: 25px;">Edit Product Details</h1>
    <br>
    <div class="row">
        <form [formGroup]='EditProductForm'>
            <div class="col-lg-4"></div>
            <div class="col-lg-6">
                <div class="row">
                    <div class="col-lg-4">
                        <h5>Product Name</h5>
                    </div>
                    <div class="col-lg-6">
                        <input type="hidden" formControlName='productId'>
                        <input type="text" class="form-control" formControlName='ProductName'
                           />
                    </div>
                </div>
                <div class="row">
                    <div class="col-lg-4">
                        <h5>Description</h5>
                    </div>
                    <div class="col-lg-6">
                        <input type="text" class="form-control" formControlName='Description'
                             />
                    </div>
                </div>
                <br>
                <div class="row">
                    <div class="col-lg-4">

                    </div>
                    <div class="col-lg-6">
                        <button type="submit" class="btn btn-danger"  rounded="true">Edit</button>
                    </div>
                </div>
            </div>
        </form>
    </div>
</div>
